mapper.update:
update product_mst pm left outer join product_file pf on(pf.product_code = pm.product_code) set pm.product_code = #{product_code}, pm.product_category = #{product_category}, pm.product_name = #{product_name}, pm.product_price = #{product_price}, pm.product_explanation = #{product_explanation}, pm.product_size = #{product_size}
, pf.file_name = #{file_name}
where pm.product_code = #{product_code}
