mapper.select:
SELECT pm.product_code, pf.file_name, pm.product_name, pm.product_price, pm.product_size, pm.product_info,
cm.category_name,
tc.total_product_count FROM product_mst pm LEFT OUTER JOIN product_file pf ON(pf.product_code = pm.product_code) LEFT OUTER JOIN category_mst cm on (cm.category_code = pm.category_code) LEFT OUTER JOIN (select count(*) as total_product_count from product_mst) tc on(1 = 1) order by product_code asc limit #{index}, 10;

SELECT pm.product_code, pm.product_name, pm.product_price, pf.file_name, pmc.total_product_count FROM product_mst pm LEFT OUTER JOIN product_file pf ON(pf.product_code = pm.product_code) LEFT OUTER JOIN category_mst cm on (cm.category_code = pm.category_code) LEFT OUTER JOIN (select count(*) as total_product_count from product_mst) pmc on(1 = 1)
where pm.category_code = #{category_code}
order by product_code asc limit #{index}, #{contentCount};

mapper.update:
update product_mst pm left outer join product_file pf on(pf.product_code = pm.product_code)
set pm.product_code = #{product_code}, pf.product_category = #{product_category}, pm.product_name = #{product_name}, pm.product_price = #{product_price}, pm.product_info = #{product_info}, pm.product_size = #{product_size}
, pf.file_name = #{file_name}
where pm.product_code = #{product_code}
