mapper.select:
select um.user_code,
um.user_name, um.user_email, um.user_password, um.user_roles,
ud.user_address, ud.user_phone from user_mst um left outer join user_dtl ud on(ud.user_code = um.user_code) where um.user_email = #{useremail}

mapper.update:
update 
	user_mst
set
	user_password = #{user_password}
where
	user_email = #{user_email}
